import React, { useState } from "react";
import LandingPage from "./pages/LandingPage";
import GamePage from "./pages/GamePage";
import ResultsPage from "./pages/ResultsPage";
import cards from "./data/cards.json";
import "./components/styles/App.css";
import { DemographicContext } from "./contexts/DemographicContext";

function App() {
  const [currentPage, setCurrentPage] = useState("landing");
  const [selectedCards, setSelectedCards] = useState([]);

  const handleStartGame = () => {
    const shuffledCards = [...cards].sort(() => 0.5 - Math.random());
    setSelectedCards(shuffledCards.slice(0, 10));
    setCurrentPage("game");
  };

  const handleRestartGame = () => {
    const shuffledCards = [...cards].sort(() => 0.5 - Math.random());
    setSelectedCards(shuffledCards.slice(0, 10));
    setCurrentPage("game");
  };

  const finishGame = () => {
    setCurrentPage("results");
  };

  return (
    <DemographicContext.Provider value={demographicData}>
      <div className="app">
        {currentPage === "landing" && (
          <LandingPage onStartGame={handleStartGame} />
        )}
        {currentPage === "game" && (
          <GamePage gameCards={selectedCards} finishGame={finishGame} />
        )}
        {currentPage === "results" && (
          <ResultsPage onRestartGame={handleRestartGame} />
        )}
      </div>
    </DemographicContext.Provider>
  );
}

export default App;
